let card=[{id:1,text:"1"},{id:2,text:"2"},{id:3,text:"3"},{id:4,text:"4"},{id:5,text:"5"},{id:6,text:"6"},{id:7,text:"7"},{id:8,text:"8"},{id:9,text:"9"},{id:10,text:"10"},{id:11,text:"11"},{id:12,text:"12"}];function shuffle(t){t.sort((()=>Math.random()-.5))}function renderCards(t){const e=document.getElementById("game");shuffle(t),t.forEach((t=>{let d=document.createElement("div");d.classList.add("tile"),d.style.display="none",d.setAttribute("data-id",t.id),d.innerText=t.text;let l=document.createElement("div");l.classList.add("back"),l.style.display="block";const i=document.createElement("div");i.classList.add("card-block"),i.addEventListener("click",(()=>turnOn(t,d,l))),i.appendChild(d),i.appendChild(l),e.appendChild(i)}))}function turnOn(t,e,d){"block"===d.style.display?(d.style.display="none",e.style.display="block",console.log("1")):(d.style.display="block",e.style.display="none",console.log("2"))}renderCards(card);